mapper.select:
select *
from
(
select rownum as RNUM, B.*
from
(
select A.*
from member A

order by mem_No desc
)B
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM, B.* from ( select A.* from (select m.*, e.GRANT_FLAG from member m, expert_grant e where m.user_id = e.user_id) A
where
and
grant_flag = 'N' and type='승인대기' order by mem_No desc )B ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
select rownum as RNUM, B.*
from
(
select A.*
from product A

order by REGDATE desc
)B
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
select rownum as RNUM, B.*
from
(
select A.*
from admin A

order by admin_No desc
)B
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM, B.* from ( select A.* from (select p.pd_no,f.user_id,p.cate_type,p.pd_title,p.regdate, p.del_type, p.read_count, p.lang, p.frame, f.form_no, f.form_title, f.form_content, f.form_regdate, f.price, f.pay_flag ,f.orderstart, f.orderend from product p, form f where p.pd_no = f.pd_no) A
where
and
pay_flag='N' order by form_regdate desc )B ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
